import React, { useEffect, useState, useContext } from 'react';
import api from '../api/apiClient';
import { AuthContext } from '../contexts/AuthContext';
import PatientForm from '../components/PatientForm';

export default function Patients() {
  const { token } = useContext(AuthContext);
  const [q, setQ] = useState('');
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [creating, setCreating] = useState(false);
  const [message, setMessage] = useState(null);

  const search = async () => {
    setLoading(true);
    try {
      const res = await api.get(`/api/patients/search?q=${encodeURIComponent(q)}`);
      setPatients(res.data);
    } catch (err) {
      console.error(err);
      setMessage('Search failed');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    search(); // initial
    // eslint-disable-next-line
  }, []);

  const handleCreate = async (payload) => {
    try {
      const res = await api.post('/api/patients', payload);
      setMessage('Patient created');
      setCreating(false);
      setPatients(prev => [res.data, ...prev]);
    } catch (err) {
      console.error(err);
      setMessage('Create failed');
    }
  };

  return (
    <div>
      <h2>Patients</h2>
      <div style={{ display: 'flex', gap: 8, marginBottom: 12 }}>
        <input value={q} onChange={(e) => setQ(e.target.value)} placeholder="Search patients by name/phone/email" />
        <button onClick={search}>Search</button>
        <button onClick={() => setCreating(c => !c)}>{creating ? 'Cancel' : 'New patient'}</button>
      </div>

      {creating && (
        <div style={{ marginBottom: 12 }}>
          <PatientForm onSubmit={handleCreate} />
        </div>
      )}

      {loading ? <div>Loading...</div> : (
        <div>
          {message && <div style={{ marginBottom: 8 }}>{message}</div>}
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th style={{ textAlign: 'left', borderBottom: '1px solid #ddd' }}>Name</th>
                <th style={{ textAlign: 'left', borderBottom: '1px solid #ddd' }}>Contact</th>
                <th style={{ textAlign: 'left', borderBottom: '1px solid #ddd' }}>DOB</th>
              </tr>
            </thead>
            <tbody>
              {patients.map(p => (
                <tr key={p._id}>
                  <td style={{ padding: 8 }}>{p.firstName} {p.lastName}</td>
                  <td style={{ padding: 8 }}>{p.contact?.phone} / {p.contact?.email}</td>
                  <td style={{ padding: 8 }}>{p.dob ? new Date(p.dob).toLocaleDateString() : '-'}</td>
                </tr>
              ))}
              {patients.length === 0 && <tr><td colSpan="3">No patients</td></tr>}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
